import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import axios from '../config/axiosConfig';

const CsvProcessor = () => {
  const [error, setError] = useState(null);
  const [fields, setFields] = useState([]);
  const [options, setOptions] = useState([]);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    if(options){
        setFields(['title', 'description', 'release_date', 'genre']);
    }
  },[options]);
  const handleFileUpload = (event) => {
    const file = event.target.files[0];

    if (file) {
      Papa.parse(file, {
        header: true,
        skipEmptyLines: true,
        complete: (results) => {
          setOptions(results.meta.fields);
          setError(null);
        },
        error: (err) => {
          setError(err.message);
        },
      });
    }
  };

  const handleCsvUpload = async (e) => {
    e.preventDefault();
    if( fields.length != Object.keys(formData).length){
        alert('Please select all fields');
    }
    else{
        try{
          const csv = e.target.csvFile.files[0];
          console.log(csv);
          formData.append('csvFile', csv);
          console.log(formData);
            const response = await axios.post('/upload_files', { formData });
            if (response.status === 200) {
                //navigate('/movies');
            }
            else{
                alert("Something went wrong. Please try again later!");
            }
        }catch(e){
          console.log(e);
          alert('Something went wrong!');
        }
    }
  };


  const handleSelectChange = (e) => {
        setFormData((pre) => ({ ...pre, [e.target.name]: e.target.value}))
  };

  return (
    <div>
      <h1>CSV Header Processor</h1>
      <input type="file" accept=".csv" onChange={handleFileUpload} id="csvFile" />
      {error && <p style={{ color: 'red' }}>Error: {error}</p>}
      {options.length > 0 && (
        <div>
          <h2>Headers</h2>
          <ul>
            {options.map((option, index) => (
              <li key={index}>{option}</li>
            ))}
          </ul>
          <table>
            <tbody>
          {fields.map(field =>(
            <tr>
                <td>{field}</td>
                <td>
                    {/* <CsvSelect options={options}/> */}
                    <select className="form-control" name={field} id={field} onChange={handleSelectChange}>
                        <option value="">-- Select --</option>
                        {options.map((option) => (
                            <option value={option}>
                                {option}
                            </option>
                        ))}
                    </select>
                </td>
            </tr>
          ))}
            <tr>
                <td></td>
                <td><button className="btn btn-primary float-right ml-3" type='button' onClick={handleCsvUpload}>Upload</button></td>
            </tr>
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default CsvProcessor;
