import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import axios from '../config/axiosConfig';


const Upload = () => {
    const { register, handleSubmit, formState: { errors } } = useForm();
    const [uploadStatus, setUploadStatus] = useState(null);

    const onSubmit = async (data) => {
        const formData = new FormData();
        formData.append('csvFile', data.csvFile[0]);
        console.log(formData);
        try {
            const response = await axios.post('/bulkupload', {formData : formData});
            // const uploadData = await response.json();
            // setUploadStatus(uploadData.message);
        } catch (error) {
            console.error('Error uploading CSV:', error);
            setUploadStatus('Error uploading CSV. Please try again.');
        }
    };

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <input type="file" {...register('csvFile', { required: true })} />
            {errors.csvFile && <span>This field is required</span>}
            <button type="submit">Upload</button>
            {uploadStatus && <p>{uploadStatus}</p>}
        </form>
    );
};

export default Upload;
