import React, { useState, useEffect } from 'react';
import CsvSelect from './CsvSelect';
import Papa from 'papaparse';

const CsvProcessor = () => {
  const [error, setError] = useState(null);
  const [fields, setFields] = useState([]);
  const [options, setOptions] = useState([]);

  useEffect(() => {
    if(options){
        setFields(['title', 'description', 'release_date', 'genre']);
    }
  },[options]);
  const handleFileUpload = (event) => {
    const file = event.target.files[0];

    if (file) {
      Papa.parse(file, {
        header: true,
        skipEmptyLines: true,
        complete: (results) => {
          setOptions(results.meta.fields);
          setError(null);
        },
        error: (err) => {
          setError(err.message);
        },
      });
    }
  };

  const handleCsvUpload = () => {

  };


  const hsndleSelectChange = (key, e) => {

  };

  return (
    <div>
      <h1>CSV Header Processor</h1>
      <input type="file" accept=".csv" onChange={handleFileUpload} />
      {error && <p style={{ color: 'red' }}>Error: {error}</p>}
      {options.length > 0 && (
        <div>
          <h2>Headers</h2>
          <ul>
            {options.map((option, index) => (
              <li key={index}>{option}</li>
            ))}
          </ul>
          <table>
          {fields.map(field =>(
            <tr>
                <td>{field}</td>
                <td>
                    {/* <CsvSelect options={options}/> */}
                    <select name={field} id={field} onChange={hsndleSelectChange.bind(null, field)}>
                        <option value="">-- Select --</option>
                        {options.map((option) => (
                            <option value={option}>
                                {option}
                            </option>
                        ))}
                    </select>
                </td>
            </tr>
          ))}
            <tr>
                <td></td>
                <td><button onClick={handleCsvUpload}>Upload</button></td>
            </tr>
          </table>
        </div>
      )}
    </div>
  );
};

export default CsvProcessor;
