// Dashboard.js
import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import Menu from './Menu';
import axios from '../config/axiosConfig';


const Form = ({ heading ,buttonName }) => {
    const [title, setTitle] = useState([]);
    const [description, setDescription] = useState([]);
    const [date, setDate] = useState([]);
    const [genre, setGenre] = useState([]);
    const [selectedGenreId, setSelectedGenreId] = useState([]);
    const [token, setToken] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('authToken');
        if( !token ) {
            navigate('/login');
        }
        else{
            setToken(token);
        }
    }, []);
    const handleButton = () =>{
        navigate('/createmovie');
    }

  return (
    <>
    <div className="card-header">
    <strong className="card-title">{ heading }</strong>
    </div>

    <div className="card-body">
        <div className="row">
            <div className="col-md-6">
                <div className="form-group mb-3">
                    <label htmlFor="title">Movie Title</label>
                    <input 
                        type="text" 
                        id="title" 
                        className="form-control" 
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                    />
                </div>
                <div className="form-group mb-3">
                    <label htmlFor="release_date">Release Date</label>
                    <input 
                        type="date" 
                        id="release_date" 
                        className="form-control" 
                        value={date}
                        onChange={(e) => setDate(e.target.value)}
                    />
                </div>
                <div className="form-group mb-3">
                    <label htmlFor="genre">Genre</label>
                    <select className="form-control" id="genre" value={selectedGenreId} onChange={handleSelectChange}>
                        <option value="Action">Action</option>
                        <option value="Comedy">Comedy</option>
                        <option value="Drama">Drama</option>
                        <option value="Romantic">Romantic</option>
                    </select>
                </div>
            </div>
            <div className='col-md-6'>
                <div class="form-group mb-3">
                    <label for="example-textarea">Description</label>
                    <textarea 
                        class="form-control" 
                        id="example-textarea" 
                        rows="4"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                    ></textarea>
                </div>

                <div className="form-group mb-3" >
                    <button type="submit" className="btn btn-primary" onClick={handleButton}>
                        {buttonName}
                    </button>
                </div>
            </div>

        </div>
    </div>
    </>
  );
};

export default Form;
