import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Papa from "papaparse";
import axios from "../config/axiosConfig";
import { jwtDecode } from "jwt-decode";

const CsvProcessor = (props) => {
  const [error, setError] = useState(null);
  const [fields, setFields] = useState([]);
  const [options, setOptions] = useState([]);
  const [formData, setFormData] = useState({});
  const [file, setFile] = useState({});
  const [csvValues, setCsvValues] = useState({});
  const [userId, setUserId] = useState("");
  const [defectiveRecords, setDefectiveRecords] = useState({});
  const [inputs, setInputs] = useState([]);
  const [values, setValues] = useState([]);

  const navigate = useNavigate();

  // const [sDefectiveRows, setsDefectiveRows] = useState({
  //   1: {
  //     name: "Movie 1",
  //     demodemo: "This is sample moview 1",
  //     releaseDate: "12/10/2024",
  //     genre: "drama",
  //   },
  //   2: {
  //     name: "Movie 2",
  //     description: "This is sample moview 2",
  //     releaseDate: "12/11/2024",
  //     genre: "comedy",
  //     asdfasdf: "asdfasdf",
  //   },
  // });

  const [sDefectiveRows, setsDefectiveRows] = useState({});

  useEffect(() => {
    let { id } = jwtDecode(localStorage.getItem("authToken"));
    setUserId(id);
    if (options) {
      setFields(props.fields); // Setting collection fields name from props
    }
  }, [options]);

  function myfun() {
    if (Object.keys(defectiveRecords).length > 0) {
      let inputs = [];
      for (let defectiveRecord in defectiveRecords) {
        console.log(`Recieved`, defectiveRecord);
        if (fields.length > 0) {
          debugger;
          let table = "<tr>";
          for (let field in fields) {
            table +=
              ` <td>
                          <input 
                            key={defectiveRecord} 
                            type="text" 
                            value="` +
              defectiveRecords[defectiveRecord].title +
              `"
                            onChange="` +
              handleInputChange(field, field) +
              `"
                          />
                        </td>`;
          }
          table += "</tr>";
          inputs.push(table);
        }
      }
      return (
        <tbody
          className="table table-bordered"
          dangerouslySetInnerHTML={{ __html: inputs }}
        ></tbody>
      );
    }
  }

  const handleInputChange = (i, event) => {
    //alues[i] = event.target.value;
    //setValues([...values]);
    debugger;
  };

  const handleFileUpload = (event) => {
    setFile(event.target.files[0]);
    const csvFile = event.target.files[0];
    if (csvFile) {
      Papa.parse(csvFile, {
        header: true,
        skipEmptyLines: true,
        complete: (results) => {
          setOptions(results.meta.fields); // setting dropdown option from csv file
          setError(null);
          setCsvValues(results);
        },
        error: (err) => {
          setError(err.message);
        },
      });
    }
  };

  const handleCsvUpload = async (e) => {
    e.preventDefault();
    if (fields.length != Object.keys(formData).length) {
      alert("Please select all fields");
    } else {
      try {
        let transformedObject = [];

        for (const i in csvValues.data) {
          let transformedObjectTemp = {};
          //transformedObjectTemp["user_id"] = userId;
          for (const key in csvValues.data[i]) {
            if (formData.hasOwnProperty(key)) {
              transformedObjectTemp[formData[key]] = csvValues.data[i][key];
            }
            // else {
            //   // Handle keys not present in the first object (optional)
            //   console.warn(`Ignoring key "${key}" from the second object.`);
            // }
          }
          transformedObject.push({ transformedObjectTemp });
        }
        let filteredData = transformedObject.map(
          (obj) => obj.transformedObjectTemp
        );

        const response = await axios.post(props.url, { movies: filteredData });
        if (response.status === 200) {
          alert(response.data.data.insertedRow+" uploaded successfully");
          if (Object.keys(response.data.data.defectiveRecords[0]).length != 0) {
            setOptions([]);
            setsDefectiveRows(response.data.data.defectiveRecords[0]);
          }
        } else {
          alert("Something went wrong. Please try again later!");
        }
      } catch (e) {
        console.log(e);
        alert("Something went wrong!");
      }
    }
  };

  const handleSelectChange = (e) => {
    setFormData((pre) => ({ ...pre, [e.target.value]: e.target.name }));
  };



  const handleItemChange = (element, rowId, itemId) => {

    setsDefectiveRows((prevDefects) => {
      let _prevDefects = {...prevDefects};
      _prevDefects[rowId][itemId] = element.target.value;
      return _prevDefects;
    });

  };

  return (
    <div>
      <h1>CSV Header Processor</h1>
      <input
        type="file"
        accept=".csv"
        onChange={handleFileUpload}
        id="csvFile"
        name="csvFile"
      />
      {error && <p style={{ color: "red" }}>Error: {error}</p>}
      {options.length > 0 && (
        <div>
          <table>
            <tbody>
              {fields.map((field) => (
                <tr>
                  <td>{field}</td>
                  <td>
                    {/* <CsvSelect options={options}/> */}
                    <select
                      className="form-control"
                      name={field}
                      id={field}
                      onChange={handleSelectChange}
                    >
                      <option value="">-- Select --</option>
                      {options.map((option) => (
                        <option value={option}>{option}</option>
                      ))}
                    </select>
                  </td>
                </tr>
              ))}
              <tr>
                <td></td>
                <td>
                  <button
                    className="btn btn-primary float-right ml-3"
                    type="button"
                    onClick={handleCsvUpload}
                  >
                    Upload
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      )}

      {Object.keys(sDefectiveRows).length > 0 && (
        <div>
          <table>
            <thead>
              <th>
                {fields.map((field) => (
                  <td>{field}</td>
              ))}
              </th>
            </thead>
            <tbody>
            {Object.entries(sDefectiveRows).map(
              ([defectRowId, defectiveRowItem]) => (
                <tr key={defectRowId}>
                  {Object.entries(defectiveRowItem).map(([itemId, item]) => (
                    <td>
                      <input
                        type="text"
                        value={item}
                        onChange={(e) =>
                          handleItemChange(e, defectRowId, itemId)
                        }
                      ></input>
                    </td>
                  ))}
                </tr>
              )
            )}

            <button className="btn btn-primary">Try Again</button>
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default CsvProcessor;
