import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Papa from 'papaparse';
import axios from '../config/axiosConfig';
import { jwtDecode } from 'jwt-decode';

const CsvProcessor = (props) => {
  const [error, setError] = useState(null);
  const [fields, setFields] = useState([]);
  const [options, setOptions] = useState([]);
  const [formData, setFormData] = useState({});
  const [file, setFile] = useState({});
  const [csvValues, setCsvValues] = useState({});
  const [userId, setUserId] = useState('');
  const [defectiveRecords, setDefectiveRecords] = useState({});

  const navigate = useNavigate();

  useEffect(() => {
    let { id } = jwtDecode(localStorage.getItem('authToken'));
    setUserId(id);
    if(options){
        setFields(props.fields); // Setting collection fields name from props
    }
  },[options]);

  const handleFileUpload = (event) => {
    setFile(event.target.files[0]);
    const csvFile = event.target.files[0];
    if (csvFile) {
      Papa.parse(csvFile, {
        header: true,
        skipEmptyLines: true,
        complete: (results) => {
          setOptions(results.meta.fields);// setting dropdown option from csv file
          setError(null);
          setCsvValues(results);
        },
        error: (err) => {
          setError(err.message);
        },
      });
    }
  };

  const handleCsvUpload = async (e) => {
    e.preventDefault();
    if( fields.length != Object.keys(formData).length){
        alert('Please select all fields');
    }
    else{
        try{
            let transformedObject = [];

              for (const i in csvValues.data){
                let transformedObjectTemp = {};
                transformedObjectTemp['user_id'] = userId;
                for (const key in csvValues.data[i]) {
                  if (formData.hasOwnProperty(key)) {
                    transformedObjectTemp[formData[key]] = csvValues.data[i][key];
                  }
                  // else {
                  //   // Handle keys not present in the first object (optional)
                  //   console.warn(`Ignoring key "${key}" from the second object.`);
                  // }

                }
                  transformedObject.push({ transformedObjectTemp });
              }
              let filteredData =  transformedObject.map(obj => obj.transformedObjectTemp);

              const response = await axios.post(props.url, { movies : filteredData});
              if (response.status === 200) {
                  alert('Data uploaded successfully');
                  if(defectiveRecords.length == 0){
                    navigate('/movies');
                  }
              }
              else{
                  alert("Something went wrong. Please try again later!");
              }
        }catch(e){
          console.log(e);
          alert('Something went wrong!');
        }
    }
  };





  const handleSelectChange = (e) => {
        setFormData((pre) => ({ ...pre, [e.target.value]: e.target.name}))
  };

  const handleChange = (event, index) => {
    debugger
    const newErrorFilteredData = [...errorFilteredData]; // Create a copy of the state
    newErrorFilteredData[index] = {
      ...newErrorFilteredData[index], // Spread the existing object at the index
      [event.target.name]: event.target.value, // Update only the specific property
    };
    setErrorFilteredData(newErrorFilteredData); // Update state using the setter function
  };

  return (
    <div>
      <h1>CSV Header Processor</h1>
      <input type="file" accept=".csv" onChange={handleFileUpload} id="csvFile" name="csvFile" />
      {error && <p style={{ color: 'red' }}>Error: {error}</p>}
      {options.length > 0 && (
        <div>

          <table>
            <tbody>
          {fields.map(field =>(
            <tr>
                <td>{field}</td>
                <td>
                    {/* <CsvSelect options={options}/> */}
                    <select className="form-control" name={field} id={field} onChange={handleSelectChange}>
                        <option value="">-- Select --</option>
                        {options.map((option) => (
                            <option value={option}>
                                {option}
                            </option>
                        ))}
                    </select>
                </td>
            </tr>
          ))}
            <tr>
                <td></td>
                <td><button className="btn btn-primary float-right ml-3" type='button' onClick={handleCsvUpload}>Upload</button></td>
            </tr>
            </tbody>
          </table>


        </div>
      )}

      {defectiveRecords.length > 0 && (
          <div>
            <table>
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Description</th>
                  <th>Release Date</th>
                  <th>Genre</th>
                </tr>
              </thead>
              <tbody className='table table-bordered'>
              {defectiveRecords.map((person, index) => (
                <tr key={index}>
                 <td><input type='text' value={errorFilteredData[index].title} onChange={(event) => handleChange(event, index)}></input></td>
                 <td><input type='text' value={errorFilteredData[index].description} onChange={(event) => handleChange(event, index)}></input></td>
                 <td><input type='date' value={errorFilteredData[index].release_date} onChange={(event) => handleChange(event, index)}></input></td>
                 <td><input type='text' value={errorFilteredData[index].genre} onChange={(event) => handleChange(event, index)}></input></td>
                </tr>
              ))}
              </tbody>
            </table>
          </div>
      )}
    </div>
  );
};

export default CsvProcessor;
